const BlockType = require('../../extension-support/block-type');
const formatMessage = require('format-message');
const log = require('../../util/log');
const xhr = require('xhr');

/**
 * Icon svg to be displayed at the left edge of each extension block, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const blockIconURI =
    'data:image/png;base64,';


/**
 * @param {Runtime} runtime - the runtime instantiating this block package.
 * @constructor
 */
class Scratch3HomeAssistant {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;

        this._hassHost = 'https://homeassistant.bachi:8123';
        this._hassToken = 'ADD YOUR TOKEN HERE';
        this._hassEntity = 'group.YOURGROUP';
    }

    /**
     * @returns {object} metadata for this extension and its blocks.
     */
    getInfo () {
        return {
            id: 'homeassistant',
            name: formatMessage({
                id: 'homeassistant.categoryName',
                default: 'HomeAssistant',
                description: 'Home control Blocks'
            }),
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'haTurnOn',
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: 'homeassistant.turnOn',
                        default: 'Turn On',
                        description: 'turn on an entity'
                    })
                },
                {
                    opcode: 'haTurnOff',
                    blockType: BlockType.COMMAND,
                    text: formatMessage({
                        id: 'homeassistant.turnOff',
                        default: 'Turn Off',
                        description: 'turn off an entity'
                    })
                }
            ]
        };
    }

    haTurnOn (args, util) {
        log.warn(`Going to turn ON`);
        this._doRequest(this._hassHost + '/api/services/homeassistant/turn_on');
    }

    haTurnOff (args, util) {
        log.warn(`Going to turn OFF`);
        this._doRequest(this._hassHost + 'api/services/homeassistant/turn_off');
    }

    _doRequest (url) {
        xhr(
            {
                method: 'post',
                url,
                body: '{"entity_id": '+this._hassEntity+'}',
                withCredentials: true,
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization':
                        'Bearer ' + this._hassToken
                }
            },
            (err, response) => {
                if (err) {
                    log.error(err);
                    return;
                }
                if (response.statusCode !== 200) {
                    log.error('Request Failed');
                }
                let body;
                try {
                    // Since we didn't set json: true, we have to parse manually
                    // body = JSON.parse(response.body);
                } catch (e) {
                    log.error('Request Failed');
                    return;
                }
                log.error('Request Success');
            }
        );
    }
}

module.exports = Scratch3HomeAssistant;
